/*Table structure for table `t_sms_log` */

DROP TABLE IF EXISTS `t_sms_log`;

CREATE TABLE `t_sms_log` (
  `sms_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '短信发送ID',
  `sms_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '短信分类（1：账户注册｜2：找回密码｜3：绑定第三方验证手机｜4：绑定银行卡或支付账号｜5：消息提醒类型的短信）',
  `sms_code` char(6) NOT NULL DEFAULT '' COMMENT '验证码',
  `sms_mobile` char(11) NOT NULL DEFAULT '' COMMENT '接收手机号码',
  `sms_content` varchar(255) NOT NULL DEFAULT '' COMMENT '短信内容',
  `sms_confirm_code` char(3) NOT NULL DEFAULT '' COMMENT '确认码',
  `sms_linked_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '短信关联ID',
  `sms_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '短信发送时间',
  `sms_ip` char(15) NOT NULL DEFAULT '' COMMENT '请求发送的IP',
  `result_code` varchar(15) NOT NULL DEFAULT '' COMMENT '发送接口返回码',
  `fail_count` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '验证码试错次数',
  `sms_success` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `is_only_push` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否仅需要push.不需要sms。（0:仅push|1:push和sms)',
  PRIMARY KEY (`sms_id`),
  KEY `sms_type` (`sms_type`,`sms_mobile`),
  KEY `sms_ip` (`sms_ip`,`sms_time`),
  KEY `sms_success` (`sms_type`,`sms_success`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='短信记录表';

